public class StringExampleIndexPositon {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "java exersise";
		int index1 = s.charAt(0);
		int index2 = s.charAt(5);
		System.out.println("The index position 0 "+(char)index1);
		System.out.println("The index position 5 "+(char)index2);
		 

	}

}
